import { Location } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ActivatedRoute, Router } from '@angular/router';

import { RegisterService } from '../services/register.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent  implements OnInit{
  form: FormGroup;
  constructor( private formBuilder: FormBuilder,
    private service:RegisterService,
    private snackBar: MatSnackBar,
    private location:Location,
    private router: Router,
    private route : ActivatedRoute ) {
      this.form = this.formBuilder.group( {
          id:[null],
          name:[null],
          email:[null],
          password:[null]


      });

  }

  ngOnInit(): void {
  }

 onSubmit (){
    this.service.save(this.form.value)
    .subscribe( result => this.onSucess(), error => this.onError()

    );


 }
 onCancel() {
  this.location.back();


 }

 private onSucess(){
  this.snackBar.open('Cadastro realizado com sucesso!','',{duration:5000});
  this.router.navigate(['/login'], {relativeTo:this.route});
 }
 private onError () {
  this.snackBar.open('Erro ao salvar usuario','',{duration:5000});
 }


}
<mat-card>
  <mat-toolbar class="title">Cadastro</mat-toolbar>

  <mat-card-content>
    <form [formGroup]="form" class="form">
      <mat-form-field class="full-width" appearance="fill">
        <input matInput placeholder="Nome" formControlName="name">
      </mat-form-field>

      <mat-form-field class="full-width" appearance="fill">
        <input matInput placeholder="E-mail" formControlName="email">
      </mat-form-field>

      <mat-form-field class="full-width" appearance="fill">
        <input matInput placeholder="Senha" formControlName="password">
      </mat-form-field>
    </form>

  </mat-card-content>
  <mat-card-actions>
    <button mat-raised-button (click)="onSubmit()" color="primary">Salvar</button>
    <button mat-raised-button (click)="onCancel()">Cancelar</button>
  </mat-card-actions>

</mat-card>
