import { Component, OnInit} from '@angular/core';
import { Usuario } from '../model/usuario'
import { UsuarioService } from '../services/usuario.service';

import { ActivatedRoute, Router } from '@angular/router';
import { catchError, Observable, of } from 'rxjs';
import { ErrorDialogComponent } from 'src/app/shared/components/error-dialog/error-dialog.component';
import { MatDialog } from '@angular/material/dialog';

@Component({
  selector: 'app-conta',
  templateUrl: './conta.component.html',
  styleUrls: ['./conta.component.scss']
})
export class ContaComponent {
usuario$:Observable<Usuario[]>;
displayedColumns = ['name', 'email','password'];

  constructor(
    private usuario: UsuarioService,
    private router:Router,
    private route: ActivatedRoute,
    public dialog: MatDialog )
  {
    {

        this.usuario$ = this.usuario.list().pipe(
            catchError((error) => {
              this.onErro('Erro ao carregar. ');
              return of([]);
            })
          );
  }

}

ngOnInit(): void {

  }
onErro(errorMsg: string) {
    this.dialog.open(ErrorDialogComponent, {
      data: errorMsg,
    });
  }

}
<mat-card >
  <mat-toolbar color="primary">
    <span>Servicos Cadastrados</span>
  </mat-toolbar>
  <div *ngIf="servicos$ | async as servicos; else loading " >
    <table mat-table [dataSource]="servicos" class="mat-elevation-z8">

      <ng-container matColumnDef="descricao">
        <th mat-header-cell *matHeaderCellDef> Serviço </th>
        <td mat-cell *matCellDef="let servico"> {{  servico.descricao  }} </td>
      </ng-container>
      <ng-container matColumnDef="valor">
        <th mat-header-cell *matHeaderCellDef> Valor </th>
        <td mat-cell *matCellDef="let servico"> {{servico.valor}} </td>
      </ng-container >
      <ng-container matColumnDef="foto" >
        <th mat-header-cell *matHeaderCellDef> Foto </th>
        <td class="foto" mat-cell *matCellDef="let servico"> {{servico.foto}} </td>
      </ng-container >

      <ng-container matColumnDef="actions">
       <mat-header-cell *matHeaderCellDef>
        <button mat-mini-fab color="accent" aria-label="adicionar serviço">
          <mat-icon  (click)=" onAdd()">add</mat-icon>
        </button>
       </mat-header-cell>

       <mat-cell *matCellDef="let servico">
        <button mat-icon-button  (click)=" onEdit(servico)" color="primary" aria-label="Editar  serviço">
          <mat-icon>edit</mat-icon>
        </button>
        <button mat-icon-button color="warn" (click)="onDelete(servico)"   aria-label="Remover  serviço">
          <mat-icon>delete</mat-icon>
        </button>
       </mat-cell>

      </ng-container>


      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
      <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
    </table>
  </div>

  <ng-template #loading >
    <div class="loading-spinner">
        <mat-spinner></mat-spinner>
      </div>
  </ng-template>

</mat-card>
