import { Component, OnInit} from '@angular/core';
import { Usuario } from '../model/usuario'
import { UsuarioService } from '../services/usuario.service';

import { ActivatedRoute, Router } from '@angular/router';
import { catchError, Observable, of } from 'rxjs';
import { ErrorDialogComponent } from 'src/app/shared/components/error-dialog/error-dialog.component';
import { MatDialog } from '@angular/material/dialog';

@Component({
  selector: 'app-conta',
  templateUrl: './conta.component.html',
  styleUrls: ['./conta.component.scss']
})
export class ContaComponent {

displayedColumns = ['name', 'email','password',];
usuarios:any

  constructor(
    private usuario: UsuarioService,
    private router:Router,
    private route: ActivatedRoute,
    public dialog: MatDialog )
  {
    {

      this.usuario.list().pipe(
        catchError((error) => {
          this.onErro('Erro ao carregar. ');
          return of([]);
        })
      ).subscribe(
        (usuarios) => {
          this.usuarios = Array.isArray(usuarios) ? usuarios : [usuarios];
        }
      );

  }


}

ngOnInit(): void {

  }
onErro(errorMsg: string) {
    this.dialog.open(ErrorDialogComponent, {
      data: errorMsg,
    });
  }

}
<mat-card >
  <mat-toolbar color="primary">
    <span>Servicos Cadastrados</span>
  </mat-toolbar>
  <div  >
    <table mat-table [dataSource]="usuarios" class="mat-elevation-z8">

      <ng-container matColumnDef="name">
        <th mat-header-cell *matHeaderCellDef> Nome </th>
        <td mat-cell *matCellDef="let usuarios"> {{  usuarios.name }} </td>
      </ng-container>
      <ng-container matColumnDef="email">
        <th mat-header-cell *matHeaderCellDef> Email </th>
        <td mat-cell *matCellDef="let usuarios"> {{usuarios.email}} </td>
      </ng-container >
      <ng-container matColumnDef="password" >
        <th mat-header-cell *matHeaderCellDef> Senha </th>
        <td class="foto" mat-cell *matCellDef="let usuarios"> {{usuarios.password}}</td>
      </ng-container >

      <ng-container matColumnDef="actions">
       <mat-header-cell *matHeaderCellDef>
        <button mat-mini-fab color="accent" aria-label="adicionar serviço">
          <mat-icon  >add</mat-icon>
        </button>
       </mat-header-cell>

       <mat-cell *matCellDef="let servico">
        <button mat-icon-button   color="primary" aria-label="Editar  serviço">
          <mat-icon>edit</mat-icon>
        </button>
        <button mat-icon-button color="warn"   aria-label="Remover  serviço">
          <mat-icon>delete</mat-icon>
        </button>
       </mat-cell>

      </ng-container>


      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
      <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
    </table>
  </div>

  <ng-template #loading >
    <div class="loading-spinner">
        <mat-spinner></mat-spinner>
      </div>
  </ng-template>

</mat-card>
