import { Location } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ActivatedRoute } from '@angular/router';
import { AuthService } from 'auth.service';

import { Project } from '../model/project';
import { ProjectService } from '../services/project.service';
import { DatePipe } from '@angular/common';

@Component({
  selector: 'app-projeto-form',
  templateUrl: './projeto-form.component.html',
  styleUrls: ['./projeto-form.component.scss']
})
export class ProjetoFormComponent implements OnInit {
  form: FormGroup;

  constructor( private formBuilder: FormBuilder,
    private service: ProjectService,
    private snackBar: MatSnackBar,
    private location:Location,
    private route :ActivatedRoute,
    private authService : AuthService) {
      this.form = this.formBuilder.group( {
          id:[null],
          name:[null],
          description:[null],
          id_User:this.authService.Usuario,
          createdAt:[null],
          updatedAt:[null],

      });

  }

  ngOnInit()  {
    const projeto: Project[]  = this.route.snapshot.data['projeto'];
    const createdAt = new DatePipe('en-US').transform(projeto[0].createdAt, 'dd/MM/yyyy HH:mm:ss');
    const updatedAt = new DatePipe('en-US').transform(projeto[0].updatedAt, 'dd/MM/yyyy HH:mm:ss');
    this.form.setValue ({
      id:projeto[0].id,
      name:projeto[0].name,
      description:projeto[0].description,

      id_User:this.authService.Usuario,
      createdAt:createdAt,
      updatedAt:updatedAt,
    })
      console.log('ID do projeto:', projeto[0].id)
  }

  onSubmit (){
    this.service.save(this.form.value)
    .subscribe( result => this.onSucess(), error => this.onError());


 }
 onCancel() {
  this.location.back();


 }

 private onSucess(){
  this.snackBar.open('Serviço salvo com sucesso!','',{duration:5000});
  this.onCancel();
 }
 private onError () {
  this.snackBar.open('Erro ao salvar serviço','',{duration:5000});
 }


}

<div class="container">
      <mat-card>
        <mat-toolbar >Detalhes do Projeto</mat-toolbar>
        <mat-card-content>
          <form [formGroup]="form" class="min-width">
            <mat-form-field class="full-width">
              <mat-label>NOME</mat-label>
              <input matInput placeholder="NOME" formControlName="name">
            </mat-form-field>

            <mat-form-field class="full-width">
              <mat-label>DESCRIÇÃO</mat-label>
              <textarea name="" id="" cols="30" rows="10" matInput placeholder="DESCRIÇÃO" formControlName="description"></textarea>
            </mat-form-field>
            <mat-form-field class="full-width">
              <input matInput placeholder="ATUALIZAÇÃO" formControlName="updatedAt">
            </mat-form-field>
            <mat-form-field class="full-width">
              <input matInput placeholder="CRIAÇÃO" formControlName="createdAt">
            </mat-form-field>
          </form>
        </mat-card-content>

        <mat-card-actions>
          <button mat-raised-button color="primary" (click)="onSubmit()">Salvar</button>
          <button mat-raised-button (click)="onCancel()">Cancelar</button>
        </mat-card-actions>
      </mat-card>
      </div>
