import { Location } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ActivatedRoute, Router } from '@angular/router';

import { LoginService } from '../services/login.service';

import jwt_decode from 'jwt-decode';
import { BehaviorSubject } from 'rxjs';
import { AuthService } from '../../../../auth.service';
import { User } from '../model/login';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent implements OnInit {
  form: FormGroup;
  constructor(
    private service: LoginService,
    private formBuilder: FormBuilder,
    private snackBar: MatSnackBar,
    private location: Location,
    private router: Router,
    private route: ActivatedRoute,
    private authService:AuthService
  ) {
    this.form = this.formBuilder.group({
      email: [null],
      password: [null],
    });
  }

  ngOnInit(): void {

  }
  onLogar() {
    this.authService.login(this.form.get('email')?.value, this.form.get('password')?.value).subscribe(
      (result) => {
        this.onSucess();
      },
      (error) => this.onError()
    );
  }
  onCancel() {
    this.location.back();
  }

  private onSucess() {
    this.snackBar.open('Login realizado com sucesso!', '', {
      duration: 5000,

    });
    this.router.navigate(['www.google.com'], { relativeTo: this.route });

  }
  private onError() {
    this.snackBar.open('Erro ao efetuar login!!!', '', { duration: 5000 });
    this.form
  }





}
<mat-card>
  <mat-toolbar  class="title">Login</mat-toolbar>
  <mat-card-content>
    <form  [formGroup]="form" class="min-width">
      <mat-form-field  class="full-width" appearance="fill">
        <input matInput placeholder="E-mail" formControlName="email">
      </mat-form-field>
      <mat-form-field class="full-width" appearance="fill">
        <input matInput placeholder="password" formControlName="password">
      </mat-form-field>
    </form>
    <mat-card-actions >

      <button  class="acoes" mat-raised-button color="primary" (click)="onLogar()">Entra</button>
      <button  class="acoes" mat-raised-button  (click)="onCancel()" >Recuperar senha</button>
  </mat-card-actions>
  </mat-card-content>

</mat-card>
