import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ActivatedRoute,Router} from '@angular/router';

import { AuthService } from 'auth.service';
import { ProjectService } from '../services/project.service';
import { Project } from '../model/project';
import { Location } from '@angular/common';

@Component({
  selector: 'app-project-form',
  templateUrl: './project-form.component.html',
  styleUrls: ['./project-form.component.scss']
})
export class ProjectFormComponent  implen+ {
  form: FormGroup;

  constructor( private formBuilder: FormBuilder,
    private service:ProjectService,
    private snackBar: MatSnackBar,
    private location:Location,
    private route :ActivatedRoute,
    private authService : AuthService) {
      this.form = this.formBuilder.group( {
          name:[null],
          description:[null],
          createdAt:[null],
          updatedAt: [null],
          id_User:authService.Usuario

      });

  }

  ngOnInit(): void {
    const projeto:Project = this.route.snapshot.data['project'];
      this.form.setValue ({
        name:projeto.name,
        description:projeto.description,
        createdAt:projeto.createdAt ,
        updatedAt: projeto.updatedAt,
        id_User :this.authService.Usuario
      })
      console.log(projeto)
  }

  onSubmit (){
    this.service.save(this.form.value)
    .subscribe( result => this.onSucess(), error => this.onError());


 }
 onCancel() {
  this.location.back();


 }

 private onSucess(){
  this.snackBar.open('Serviço salvo com sucesso!','',{duration:5000});
  this.onCancel();
 }
 private onError () {
  this.snackBar.open('Erro ao salvar serviço','',{duration:5000});
 }

}
<mat-card>
  <mat-toolbar >Detalhes do Projeto</mat-toolbar>
  <mat-card-content>
    <form [formGroup]="form" class="min-width">
      <mat-form-field class="full-width">
        <input matInput placeholder="ID" formControlName="id">
      </mat-form-field>
      <mat-form-field class="full-width">
        <input matInput placeholder="Nome" formControlName="name">
      </mat-form-field>
      <mat-form-field class="full-width">
        <input matInput placeholder="Descrição" formControlName="description">
      </mat-form-field>
      <mat-form-field class="full-width">
        <input matInput placeholder="Criação" formControlName="createdAt">
      </mat-form-field>
      <mat-form-field class="full-width">
        <input matInput placeholder="Atualização" formControlName="updatedAt">
      </mat-form-field>
    </form>
  </mat-card-content>

  <mat-card-actions>
    <button mat-raised-button color="primary" (click)="onSubmit()">Salvar</button>
    <button mat-raised-button (click)="onCancel()">Cancelar</button>
  </mat-card-actions>
</mat-card>
