import { Component, OnInit } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { ActivatedRoute, Router } from '@angular/router';
import { catchError, Observable, of } from 'rxjs';
import { ErrorDialogComponent } from 'src/app/shared/components/error-dialog/error-dialog.component';

import { Project } from '../model/project';
import { ProjectService } from '../services/project.service';


@Component({
  selector: 'app-project',
  templateUrl: './project.component.html',
  styleUrls: ['./project.component.scss']
})
export class ProjectComponent implements OnInit {
  project$: Observable<Project[]>
  displayedColumns = ['name', 'description','updatedAt','createdAt','actions'];
  projetos: Project[] = []
  constructor(
    private projetoService: ProjectService,
    private router:Router,
    private route: ActivatedRoute,
    public dialog: MatDialog,)
  {
    {
      this.project$ = this.projetoService.listByUser().pipe (

        catchError (error => {
           this.onErro('Erro ao carregar. ')
            return  of([])
        })
      );
    }




  }

  ngOnInit(): void {


  }

  onErro(errorMsg: string) {
    this.dialog.open(ErrorDialogComponent, {
      data: errorMsg,
    });
  }



  onEdit(projeto:Project) {
    this.router.navigate(['editar', projeto.id], {relativeTo:this.route});
  }

  Add() {
    this.router.navigate(['novo'], {relativeTo:this.route});
  }



  refresh() {
    this.project$= this.projetoService.list()
      .pipe(
        catchError(error => {
          this.onErro('Erro ao carregar Projetos.');
          return of([])
        })
      );
  }

  onDelete(projeto: Project) {
      this.projetoService.delete(projeto).subscribe(data => {
        this.projetos = this.projetos.filter( response => response !==projeto)
        alert("Projeto Deletado");
        this.refresh();
      })
  }
}
<div class="container">
  <mat-card >
    <mat-toolbar color="primary" >
      <span>Projetos Cadastrados</span>
    </mat-toolbar>
    <div *ngIf="project$ | async as projetos; else loading " >
      <table mat-table [dataSource]="projetos" class="mat-elevation-z8">
        <ng-container matColumnDef="name">
          <th mat-header-cell *matHeaderCellDef> Nome </th>
          <td mat-cell *matCellDef="let projetos"> {{  projetos.name }} </td>
        </ng-container>
        <ng-container matColumnDef="description">
          <th mat-header-cell *matHeaderCellDef> Descricão </th>
          <td mat-cell *matCellDef="let projetos"> {{projetos.description}} </td>
        </ng-container >
        <ng-container matColumnDef="createdAt" >
          <th mat-header-cell *matHeaderCellDef> Criação</th>
          <td mat-cell *matCellDef="let projetos"> {{projetos.createdAt | date: 'dd/MM/yyyy HH:mm:ss'}} </td>
        </ng-container >
        <ng-container matColumnDef="updatedAt" >
          <th mat-header-cell *matHeaderCellDef> Atualização </th>
          <td  mat-cell *matCellDef="let projetos"> {{projetos.updatedAt  | date: 'dd/MM/yyyy HH:mm:ss'}} </td>
        </ng-container >
        <ng-container matColumnDef="actions">
        <mat-header-cell *matHeaderCellDef>
          <button  (click)="Add()"  mat-mini-fab color="accent" aria-label="adicionar serviço">
            <mat-icon  >add</mat-icon>
          </button>
        </mat-header-cell>
        <mat-cell *matCellDef="let projeto">
          <button mat-icon-button  (click)=" onEdit(projeto)" color="primary" aria-label="Editar  serviço">
            <mat-icon>edit</mat-icon>
          </button>
          <button mat-icon-button color="warn" (click)="onDelete(projeto)"   aria-label="Remover  serviço">
            <mat-icon>delete</mat-icon>
          </button>
          <button mat-icon-button color="warn" (click)="onDelete(projeto)"   aria-label="Remover  serviço">
            <mat-icon>delete</mat-icon>
          </button>
          <button mat-icon-button [matMenuTriggerFor]="menu" aria-label="Example icon-button with a menu">
            <mat-icon>menu</mat-icon>
          </button>
          <mat-menu #menu="matMenu">
            <button mat-menu-item >
              <mat-icon>task</mat-icon>
              <span>Tarefas</span>
            </button>
          </mat-menu>
        </mat-cell>
        </ng-container>


        <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
        <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
      </table>
    </div>

    <ng-template #loading >
      <div class="loading-spinner">
          <mat-spinner></mat-spinner>
        </div>
    </ng-template>

  </mat-card>
</div>
